package ec.gob.msp.pms.cmm;

public class Ctg {

	private Integer id;
	private String opt;

	public Ctg() {
		// TODO Auto-generated constructor stub
	}

	public Ctg(int id, String opt) {
		this.id = id;
		this.opt = opt;
	}

	public Integer getId() {
		return id;
	}

	public void setId(Integer id) {
		this.id = id;
	}

	public String getOpt() {
		return opt;
	}

	public void setOpt(String opt) {
		this.opt = opt;
	}

}
